/* requires mdboostrap CSS and Js files */
class ThemedAlert{modal;alert;constructor(t,e){this.alert=$("#"+e),this.modal=t}show(){this.modal.show()}show(t,e="New Message",s="",a){let l=this.alert.find("#alert-title").removeClass("text-danger text-success text-warning text-info");switch(t){case 0:l.addClass("text-danger");break;case 1:l.addClass("text-success");break;case 2:l.addClass("text-warning");break;case 3:l.addClass("text-info");break;default:l.css("color","#8673b4")}l.text(e),this.alert.find("#alert-body").html(s),a&&this.alert.find("#confirm-alert").click((()=>location.replace(a))),this.modal.show()}}